      var aaKeyword = '';
      var url = ''
      var urlMatch = url.match('/search/label/');
      if (urlMatch != null) {
        aaKeyword = decodeURI(url.substring(urlMatch.index + 14));
      } else {
        urlMatch = url.match('/search\\?q=');
        if (urlMatch != null) {
